import React, { useEffect, useState } from 'react'
import './Home.scss'
import {db} from "../../firebase"
import { getDatabase, ref, onValue, set } from "firebase/database";
import Regadera from '../../img/regadera.png'
import Sun from '../../img/sun.png'

export default function Home(props) {

    const [fbData, setFbData] = useState({});

    useEffect(()=>{
        document.addEventListener("contextmenu", (event) => { //Bloquea el click derecho
            event.preventDefault();
        });

        const starCountRef = ref(db, 'maceta/');
        onValue(starCountRef, (snapshot) => {
            const data = snapshot.val();
            setFbData(data)
            console.log(data)
        });

    },[])

    function regar()
    {
        set(ref(db, 'maceta/regar'), 1)
        .then(()=>{

        })
    }

    function noRegar(){
        set(ref(db, 'maceta/regar'), 0)
        .then(()=>{

        })
    }


    return (
        <div className='Home'>
            <div className="humidity-container">
                <div className='humidity-fill'></div>
                <h2>75%</h2>
            </div>
            <div className='light-container'>
                <img className="sun-icon" src={Sun}/>
                <div className='light-fill' style={{width: `${(fbData.luz*100)/3000}%`}}></div>
            </div>
            <button className="regar" onMouseDown={regar} onMouseUp={noRegar}>
                <img className="regadera-icon" src={Regadera}/>
            </button>
        </div>
    )
}
